import React, { useState, useEffect } from 'react';
import './App.css';


const MazeGame = () => {
  const [maze, setMaze] = useState([
    ['#', '#', '#', '#', '#', '#', '#'],
    ['#', '.', '.', '.', '#', '.', '#'],
    ['#', '#', '#', '.', '#', '.', '#'],
    ['#', '.', '#', '.', '.', '.', '#'],
    ['#', '#', '#', '#', '#', '#', '#'],
  ]);
  const startRow = 1;
  const startCol = 1;
  const goalRow = 3;
  const goalCol = 5;

  useEffect(() => {
    solveMaze(startRow, startCol);
  }, []);

  const solveMaze = (row, col) => {
    if (!isValidPosition(row, col) || !isOpenPath(row, col)) {
      return false;
    }

    if (row === goalRow && col === goalCol) {
      const updatedMaze = [...maze];
      updatedMaze[row][col] = '*';
      setMaze(updatedMaze);
      return true;
    }

    const updatedMaze = [...maze];
    updatedMaze[row][col] = 'X';
    setMaze(updatedMaze);

    if (
      solveMaze(row - 1, col) ||
      solveMaze(row + 1, col) ||
      solveMaze(row, col - 1) ||
      solveMaze(row, col + 1)
    ) {
      return true;
    }

    updatedMaze[row][col] = '.';
    setMaze(updatedMaze);
    return false;
  };

  const isValidPosition = (row, col) => {
    return row >= 0 && row < maze.length && col >= 0 && col < maze[row].length;
  };

  const isOpenPath = (row, col) => {
    return maze[row][col] === '.';
  };

  const renderMaze = () => {
    return maze.map((row, rowIndex) => (
      <div key={rowIndex}>
        {row.map((cell, colIndex) => (
          <span key={colIndex} className={`cell ${cell === '#' ? 'wall' : ''}`}>
            {cell}
          </span>
        ))}
      </div>
    ));
  };

  return <div className="maze">{renderMaze()}</div>;
};

export default MazeGame;
